import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import './Expenses.css';

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState('2020');
    // we use useState directly in the component function

  const filterChangeHandler = selectedYear => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

    return (
      <div>
        <Card className="expenses">
        <ExpensesFilter 
          selected={filteredYear} 
          onChangeFilter={filterChangeHandler} 
        />
        {/* cannot use if/else statement or for statement in curly braces. can use ternary statement */}
        {filteredExpenses.length === 0 && <p>No expenses found.</p>}
        {filteredExpenses.length > 0 &&
          filteredExpenses.map((expense) => (
            <ExpenseItem 
              key={expense.id}
              // always add a key when mapping out a list of items so that 
              // React is aware not just how long the array is but also 
              // which items should be placed and update list more efficiently
              title={expense.title} 
              amount={expense.amount} 
              date={expense.date}
            />
        ))}
        </Card>
  </div>
    );
};

export default Expenses;
