import { useContext } from 'react';

import CartIcon from '../Cart/CartIcon';
import CartContext from '../../store/cart-context';
import classes from './HeaderCartButton.module.css';

const HeaderCartButton = (props) => {
    const cartCtx = useContext(CartContext);

    const numberOfCartItems = cartCtx.items.reduce((curNumber, item) => {
        return (
            curNumber + item.amount
        );
    }, 0);

    const btnClasses = `${classes.button} ${classes.bump}`;

    // we use .reduce not .length 
    // why? bc suppose we get 3 orders of sushi. that is one item, but .length would read it as three 
    // .reduce will transform an array of data into a single value into a single number

    return (
    <button className={btnClasses} onClick={props.onClick}>
        <span className={classes.icon}>
            <CartIcon />
        </span>
        <span>Your Cart</span>
        <span className={classes.badge}>{numberOfCartItems}</span>
    </button>
    );
};

export default HeaderCartButton;
