import { createSlice, configureStore } from '@reduxjs/toolkit';

const initialState = { counter: 0, showCounter: true };

const counterSlice = createSlice({
    name: 'counter',
    initialState,
    // the above line means the same as writing initialState: initialState
    reducers: {
        increment(state) {
            state.counter++
            // when using Redux toolkit and its functions like createSlice, you can call 
            // state.counter++ bc you cannot accidentally manipulate the existing state 
        },
        decrement(state) {
            state.counter--;
        },
        increase() {
            state.counter = state.counter + action.payload;
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
        }
    }
});

